import React, { useState } from 'react';
import '../../App.css';
import { Button, Icon, GridRow, GridColumn, Grid, Image } from 'semantic-ui-react';

const generateDish = () => {
  // Your dish generation logic here
}

const GridExampleCelled = () => {
  const [displayText, setDisplayText] = useState('');

  const handleButtonClick = () => {
    // Update the display text when the button is clicked
    setDisplayText('helloo');
    generateDish(); // Call your dish generation logic
  };

  return (
    <Grid celled>
      <GridRow>
        <GridColumn width={3}>
          <Image src='https://react.semantic-ui.com/images/wireframe/image.png' />
        </GridColumn>
        <GridColumn width={13}>
          <Image src='https://react.semantic-ui.com/images/wireframe/centered-paragraph.png' />
        </GridColumn>
      </GridRow>

      <GridRow>
        <GridColumn width={3}>
          <Image src='https://react.semantic-ui.com/images/wireframe/image.png' />
        </GridColumn>
        <GridColumn width={10}>
          <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
        </GridColumn>
        <GridColumn width={3}>
          <Image src='https://react.semantic-ui.com/images/wireframe/image.png' />
        </GridColumn>
      </GridRow>

      {/* Display the text next to the button */}
      <div>{displayText}</div>

      {/* Include the animated button */}
      <Button animated='fade' onClick={handleButtonClick}>
        <Button.Content visible>Next</Button.Content>
        <Button.Content hidden>
          <Icon name='arrow right' />
        </Button.Content>
      </Button>
    </Grid>
  );
};

export default function Products() {
  return (
    <div className='products'>
      <GridExampleCelled />
    </div>
  );
}
