import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ComposableMap, Geographies, Geography, ZoomableGroup, Marker } from 'react-simple-maps';
import worldData from '../data/world-110m.json'; // Ensure this path is correct
import './WorldMap.css'; // Import CSS file for styling

const WorldMap = () => {
  const navigate = useNavigate();
  const [tooltipContent, setTooltipContent] = useState("");

  const geoUrl = 'https://unpkg.com/world-atlas@1.1.4/world/110m.json';

  const handleMouseEnter = (geo) => {
    const { NAME } = geo.properties;
    setTooltipContent(NAME);
  };

  const handleMouseLeave = () => {
    setTooltipContent("");
  };

  return (
    <div className="map-container">
      <div className="tooltip">{tooltipContent}</div>
      <div className="map">
        <ComposableMap>
          <ZoomableGroup zoom={1}>
            <Geographies geography={geoUrl}>
              {({ geographies }) =>
                geographies.map((geo) => (
                  <Geography
                    key={geo.rsmKey}
                    geography={geo}
                    onMouseEnter={() => handleMouseEnter(geo)}
                    onMouseLeave={handleMouseLeave}
                    style={{
                      default: { fill: "#D6D6DA", outline: "none" },
                      hover: { fill: "#F53", outline: "none" },
                      pressed: { fill: "#E42", outline: "none" }
                    }}
                  />
                ))
              }
            </Geographies>
          </ZoomableGroup>
        </ComposableMap>
      </div>
    </div>
  );
};

export default WorldMap;
