import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ComposableMap, Geographies, Geography, ZoomableGroup, Marker } from 'react-simple-maps';
import { geoPath } from 'd3-geo';
import { feature } from 'topojson-client';
import worldData from '../data/world-110m.json'; // Ensure this path is correct
import { Tooltip } from 'react-tooltip';

const WorldMap = () => {
  const navigate = useNavigate();
  const [content, setContent] = useState("");
  const handleCountryClick = (countryCode) => {
    navigate(`/country/${countryCode}`);
  };

  const geoUrl = 'https://unpkg.com/world-atlas@1.1.4/world/110m.json';

  const markers = [
    {
      markerOffset: -15,
      name: "Sau Paulo",
      coordinates: [-58.3816, -34.6037]
    }
  ]
  return (
    <div style={{
      width: "100%",
      height: "100%",
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center'
    }}>
  <Tooltip place="top" type="dark" effect="float" />
      <div style={{ width: "1400px", borderStyle: 'double' }}>
        <ComposableMap data-tip="">
          {/* <ZoomableGroup zoom={1}> */}
            <Geographies geography={geoUrl}>
              {({ geographies }) =>
                geographies.map((geo) => (
                  <Geography
                    key={geo.rsmKey}
                    geography={geo}
                    onClick={() => handleCountryClick(geo.properties.ISO_A3)}
                    // onMouseEnter={() => {
                    //   const { NAME } = geo.properties;
                    //   setContent(`${NAME}`);
                    // }}
                    // onMouseLeave={() => {
                    //   setContent('');
                    // }}
                    style={{
                      hover: { fill: "#F53", outline: "none" },
                      pressed: { fill: "#E42", outline: "none" }
                    }}
                  />
                ))
              }
            </Geographies>
            {/* {markers.map(({ name, coordinates, markerOffset }) => (
              <Marker key={name} coordinates={coordinates}>
                <circle r={10} fill="#F00" stroke="#fff" strokeWidth={2}></circle>
                <text
                  textAnchor="middle" y={markerOffset}
                  style={{
                    fontFamily: "system-ui", 
                    fill: "#5D5A6D"
                  }}
                >
                  {name}
                </text>
              </Marker>
            ))} */}
          {/* </ZoomableGroup> */}
        </ComposableMap>
      </div>
    </div>
  );
};

export default WorldMap;
