import React, { useState } from 'react';
import '../../App.css';
import { Button, Icon, GridRow, GridColumn, Grid, Image, StepDescription } from 'semantic-ui-react';
import '../Cards.css';
import CardItem from '../CardItem';

const generateDish = () => {
  // Your dish generation logic here
}

const GridExampleCelled = () => {
  const [displayText, setContent] = useState('');

  const handleButtonClick = () => {
    // Update the display text when the button is clicked
    setContent(
      <div className='cards'>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            <CardItem
              src='images/img-9.jpg'
              text='Explore the hidden waterfall deep inside the Amazon Jungle'
              label='Adventure'
              path='/services'
            />
            </ul>
            </div>
      </div>
      </div>
    )
    generateDish(); // Call your dish generation logic
  };

  return (
    <>
    <Button animated='fade' styl onClick={handleButtonClick}>
      <Button.Content visible>Select Your Meal!</Button.Content>
      <Button.Content hidden>
        <Icon name='arrow right' />
      </Button.Content>
    </Button>      
    <div>{displayText}</div>
    </>
  );
};

export default function Products() {
  return (
    <div className='products'>
      <GridExampleCelled />
    </div>
  );
}
