import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ComposableMap, Geographies, Geography, ZoomableGroup, Marker } from 'react-simple-maps';
import worldData from '../data/world-110m.json'; // Ensure this path is correct

const Tooltip = ({ content, position }) => {
  if (!content) return null;
  const style = {
    position: 'absolute',
    top: `${position.y}px`,
    left: `${position.x}px`,
    backgroundColor: 'white',
    padding: '5px',
    border: '1px solid black',
    borderRadius: '3px',
    pointerEvents: 'none'
  };
  return <div style={style}>{content}</div>;
};

const WorldMap = () => {
  const navigate = useNavigate();
  const [content, setContent] = useState("");
  const [position, setPosition] = useState({ x: 0, y: 0 });

  const geoUrl = 'https://unpkg.com/world-atlas@1.1.4/world/110m.json';

  const handleMouseMove = (evt) => {
    setPosition({ x: evt.pageX, y: evt.pageY });
  };

  return (
    <div style={{
      width: "100%",
      height: "100%",
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      position: 'relative' // Ensure the tooltip is positioned relative to the container
    }}>
      <Tooltip content={content} position={position} />
      <div style={{ width: "1400px", borderStyle: 'double' }}>
        <ComposableMap data-tip="" onMouseMove={handleMouseMove}>
          <ZoomableGroup zoom={1}>
            <Geographies geography={geoUrl}>
              {({ geographies }) =>
                geographies.map((geo) => (
                  <Geography
                    key={geo.rsmKey}
                    geography={geo}
                    onMouseEnter={() => {
                      const { NAME } = geo.properties;
                      setContent(NAME);
                    }}
                    onMouseLeave={() => {
                      setContent('');
                    }}
                    style={{
                      hover: { fill: "#F53", outline: "none" },
                      pressed: { fill: "#E42", outline: "none" }
                    }}
                  />
                ))
              }
            </Geographies>
          </ZoomableGroup>
        </ComposableMap>
      </div>
    </div>
  );
};

export default WorldMap;
