import './MyFridge.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MyFridge = () => {
  const [foodName, setFoodName] = useState('');
  const [amount, setAmount] = useState('');
  const [fridgeItems, setFridgeItems] = useState([]);
  const [expanded, setExpanded] = useState({
    fruits: true,
    meats: true,
    vegetables: true,
  });

  // Fetch fridge items from the database
  useEffect(() => {
    fetchFridgeItems();
  }, []);

  // const fetchFridgeItems = async () => {
  //   try {
  //     const response = await axios.get('/api/fridge'); // Adjust the endpoint accordingly
  //     setFridgeItems(response.data);
  //   } catch (error) {
  //     console.error('Error fetching fridge items', error);
  //   }
  // };

  // const handleAddItem = async () => {
  //   const newItem = { foodName, amount };
  //   try {
  //     await axios.post('/api/fridge', newItem); // Adjust the endpoint accordingly
  //     fetchFridgeItems(); // Refresh items after adding
  //   } catch (error) {
  //     console.error('Error adding item', error);
  //   }
  //   setFoodName(''); // Clear inputs
  //   setAmount('');
  // };

  const toggleCategory = (category) => {
    setExpanded((prevExpanded) => ({
      ...prevExpanded,
      [category]: !prevExpanded[category],
    }));
  };

  return (
    <div>
      <h1>My Fridge</h1>

      {/* Input Fields */}
      <input
        type="text"
        placeholder="Enter the food here"
        value={foodName}
        onChange={(e) => setFoodName(e.target.value)}
      />
      <input
        type="text"
        placeholder="Enter the amount"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <button onClick={handleAddItem}>Add to your fridge</button>

      {/* Display fridge contents */}
      <h2>Your fridge:</h2>
      <div>
        <div onClick={() => toggleCategory('fruits')}>
          <h3>Fruits {expanded.fruits ? '-' : '+'}</h3>
        </div>
        {expanded.fruits && (
          <ul>
            {fridgeItems
              .filter((item) => item.category === 'fruits')
              .map((item) => (
                <li key={item.id}>
                  {item.name}, {item.quantity} {item.unit}
                </li>
              ))}
          </ul>
        )}
        <div onClick={() => toggleCategory('meats')}>
          <h3>Meats {expanded.meats ? '-' : '+'}</h3>
        </div>
        {expanded.meats && (
          <ul>
            {fridgeItems
              .filter((item) => item.category === 'meats')
              .map((item) => (
                <li key={item.id}>
                  {item.name}, {item.quantity} {item.unit}
                </li>
              ))}
          </ul>
        )}
        <div onClick={() => toggleCategory('vegetables')}>
          <h3>Vegetables {expanded.vegetables ? '-' : '+'}</h3>
        </div>
        {expanded.vegetables && (
          <ul>
            {fridgeItems
              .filter((item) => item.category === 'vegetables')
              .map((item) => (
                <li key={item.id}>
                  {item.name}, {item.quantity} {item.unit}
                </li>
              ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default MyFridge;
