import React, { useState, useEffect } from 'react';
import './MyFridge.css';

const MyFridge = () => {
  const [foodName, setFoodName] = useState('');
  const [amount, setAmount] = useState('');
  const [fridgeItems, setFridgeItems] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [expanded, setExpanded] = useState({
    fruits: true,
    meats: true,
    vegetables: true,
  });

  // get the external api for food category
  const getFoodCategory = async (foodName) => {
    const apiKey = '3e41632960bd2af35354380bd56e028f'; // Replace with your actual API key
    const appId = '800b89c9'; // Replace with your actual App ID
    const apiUrl = `https://trackapi.nutritionix.com/v2/search/instant/?query=${foodName}`;
    console.log(foodName, apiUrl);

    try {
      const response = await fetch(apiUrl, {
        headers: {
          "x-app-id": appId,
          "x-app-key": apiKey,
          "Content-Type": "application/json",
        }
      });

      if (!response.ok) {
        throw new Error('API request failed');
      }

      const data = await response.json();
      console.log('food data from external api', data);
      const category = data.common[0].serving_unit; // Extract food category
      const unit = data.common[0].serving_qty
      return category;

    } catch (error) {
      console.error('Error fetching food data:', error);
    }
  };

  useEffect(() => {
    fetch('http://localhost:8081/my_fridge')
      .then((res) => res.json())
      .then((data) => {
        console.log('Fetched data:', data);
        setFridgeItems(data); // Set the fetched data to fridgeItems
      })
      .catch((err) => console.log(err));
  }, []);

  const handleAddItem = async () => {
    const category = await getFoodCategory(foodName);
    console.log(category);

    const myData = {
      name: foodName,
      category: category || "unknown",
      quantity: amount,
      unit: "g"
    };
    // try {
    //   const result = await fetch('http://localhost:8081/my_fridge', {
    //     method: 'POST',
    //     headers: {
    //       'Content-Type': 'application/json'
    //     },
    //     body: JSON.stringify(myData)
    //   });

    //   const resultInJson = await result.json(); // Parse response as JSON

    //   // Update the state with the new fridge item
    //   setFridgeItems(prev => [...prev, resultInJson]);

    //   console.log('Response:', resultInJson); // Handle the result here
    // } catch (error) {
    //   console.error('Error submitting fridge item:', error);
    // }
  };

  const toggleCategory = (category) => {
    setExpanded((prevExpanded) => ({
      ...prevExpanded,
      [category]: !prevExpanded[category],
    }));
  };

  const debounce = (func, delay) => {
    let timeoutId;
    return (...args) => {
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
      timeoutId = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setFoodName(value);
    fetchSuggestions(value); // Call directly
  };

  const fetchSuggestions = async (input) => {
    if (!input) {
      setSuggestions([]); // Clear suggestions if input is empty
      return;
    }

    const apiKey = '3e41632960bd2af35354380bd56e028f'; // Replace with your actual API key
    const appId = '800b89c9'; // Replace with your actual App ID
    const apiUrl = `https://trackapi.nutritionix.com/v2/search/instant/?query=${input}`;

    try {
      const response = await fetch(apiUrl, {
        headers: {
          "x-app-id": appId,
          "x-app-key": apiKey,
          "Content-Type": "application/json",
        }
      });

      if (!response.ok) {
        throw new Error('API request failed');
      }

      const data = await response.json();
      // const suggestions = data.common.map(hint => hint.common.food_name); // Extract food names for suggestions
      setSuggestions(data);
    } catch (error) {
      console.error('Error fetching food suggestions:', error);
    }
  };

  return (
    <div className="container">
      {/* Form */}
      <div className="form">
        <div className="input-group">
          <input
            type="text"
            placeholder="Enter the food here"
            value={foodName}
            onChange={handleInputChange}
          />
          <input
            type="text"
            placeholder="Enter the amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button onClick={handleAddItem}>Add to your fridge</button>

          <div className="suggestions">
            {/* {suggestions.map((suggestion, index) => (
              <div
                key={index}
                className="suggestion-item"
                onClick={() => {
                  setFoodName(suggestion); // Set the food name to the selected suggestion
                  setSuggestions([]); // Clear suggestions after selection
                }}
              >
                {suggestion}
              </div>
            ))} */}
          </div>
        </div>
      </div>

      {/* Display fridge contents */}
      <h2>Your fridge:</h2>
      <div className="collapsible">
        <h2 onClick={() => toggleCategory("fruits")}>
          <span className="arrow">{expanded.fruits ? "▼  " : "▶  "}</span>
          Fruits
        </h2>
        <div className={`collapsible-content ${expanded.fruits ? "open" : ""}`}>
          <ul>
            {fridgeItems
              .filter((item) => item.category === "fruits")
              .map((item) => {
                // console.log(item);
                return (
                  <li key={item.id}>
                    {item.food_name}, {item.quantity} {item.unit}
                  </li>
                );
              })}
          </ul>
        </div>
      </div>
      <div className="collapsible">
        <h2 onClick={() => toggleCategory("meats")}>
          <span className="arrow">{expanded.meats ? "▼ " : "▶ "}</span>
          Meats
        </h2>
        <div className={`collapsible-content ${expanded.meats ? "open" : ""}`}>
          <ul>
            {fridgeItems
              .filter((item) => item.category === "meat")
              .map((item) => {
                // console.log(item);
                return (
                  <li key={item.id}>
                    {item.food_name}, {item.quantity} {item.unit}
                  </li>
                );
              })}
          </ul>
        </div>
      </div>
      <div className="collapsible">
        <h2 onClick={() => toggleCategory("vegetables")}>
          <span className="arrow">{expanded.vegetables ? "▼ " : "▶ "}</span>
          Vegetables
        </h2>
        <div
          className={`collapsible-content ${expanded.vegetables ? "open" : ""}`}
        >
          <ul>
            {fridgeItems
              .filter((item) => item.category === "vegetables")
              .map((item) => (
                <li key={item.id}>
                  {item.food_name}, {item.quantity} {item.unit}
                </li>
              ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default MyFridge;
